import { action, makeObservable, observable, runInAction } from "mobx";
import MessageHandler from "./MessageHandler";
import EventEmitter from "./EventEmitter";
import WalletManager from "./WalletManager";

const ew =
  "";

const delayFunction = (e = 0) => new Promise((t) => setTimeout(t, e));
const tw =
  "undefined" != typeof window &&
  window.Blob &&
  new Blob([atob(ew)], { type: "text/javascript;charset=utf-8" });
function getWorker() {
  const e = tw && (window.URL || window.webkitURL).createObjectURL(tw);
  try {
    return e
      ? new Worker(e)
      : new Worker("data:application/javascript;base64," + ew, {
          type: "module",
        });
  } finally {
    e && (window.URL || window.webkitURL).revokeObjectURL(e);
  }
}
// Reformatting the code with better naming conventions
const ScriptStatus = {
  INFO: 0,
  SUCCESS: 1,
  ERROR: 2,
  0: "INFO",
  1: "SUCCESS",
  2: "ERROR",
};
export default class ScriptManager extends EventEmitter {
  static MAX_LOG = 1e3;
  constructor(game) {
    super();
    this.game = game;
    this.script = null;
    this.isRunning = false;
    this.config = null;
    this.scriptList = [];
    this.logs = [];
    this.interval = 500;
    this.prevBetTime = Date.now();
    this.isAutoStep = true;
    this.messager = null;
    this.handleBet = () => {
      throw new Error("no bet handle");
    };
    this.initPms = null;
    this.logid = 0;

    makeObservable(this, {
      script: observable,
      isRunning: observable,
      config: observable,
      scriptList: observable,
      logs: observable.ref,
      unRegist: action,
    });
    this.start = this.start.bind(this);
    this.stop = this.stop.bind(this);
    this.unRegist = this.unRegist.bind(this);
    this.step = this.step.bind(this);

    this.enableAutoStep(true);
  }

  async initScript() {
    if (!this.initPms) {
      // TODO: fetch game scripts
      this.initPms = fetch("/game/manage/scripts/list/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ gameName: this.game.name }),
      })
        .then((response) => response.json())
        .then((scripts) => {
          this.scriptList = scripts.map(({ name, content, id, userId }) => ({
            name,
            content,
            id,
            userId,
          }));
        })
        .catch(() => {
          this.initPms = null;
        });
    }
    return this.initPms;
  }

  async delScript({ id }) {
    await fetch("/game/manage/scripts/delete/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ id }),
    });
    const index = this.scriptList.findIndex((script) => script.id == id);
    this.scriptList.splice(index, 1);
  }

  async updateScript(script) {
    const { id, name, content } = script;
    if (id == 0) {
      const { id: newId, userId } = await fetch("/game/manage/scripts/add/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ gameName: this.game.name, name, content }),
      });
      this.scriptList.push({ id: newId, userId, name, content });
    } else {
      await fetch("/game/manage/scripts/update/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ id, name, content }),
      });
      script.userId = this.game.userId;
      const index = this.scriptList.findIndex((s) => s.id == id);
      this.scriptList.splice(index, 1, script);
    }
  }

  async regist(script) {
    this.messager = new MessageHandler(new getWorker());
    this.messager.on("bet", this.handleBet);
    this.messager.on("log", (msg) => this.addLog(msg.message, msg.type));
    this.messager.on("stop", this.stop);
    this.messager.on("getHistory", () => this.emit("getHistory"));
    this.syncCurrency();
    let config = null;
    try {
      config = await this.messager.request("regist", script.content);
    } catch (error) {
      this.addLog(error.message, ow.ERROR);
    } finally {
      runInAction(() => {
        this.script = script;
        if (!this.config) {
          this.config = config;
        }
      });
    }
  }

  unRegist() {
    this.stop();
    this.script = null;
    this.logs = [];
    this.config = null;
  }

  syncCurrency() {
    const { currencyName, jackpot } = this.game;
    if (
      WalletManager.getInstance().dict[currencyName] &&
      jackpot[currencyName]
    ) {
      const { maxBetAmount, minBetAmount } = jackpot[currencyName];
      this.messager?.emit("syncCurrency", {
        maxAmount: maxBetAmount,
        minAmount: minBetAmount,
        currencyName,
        amount: WalletManager.getInstance().dict[currencyName].amount,
      });
    }
  }

  onGameEnd(result) {
    this.messager?.emit("gameEnd", result);
  }

  async start() {
    this.game.setBetStatus(true);
    this.isRunning = true;
    if (!this.messager && this.script) {
      this.regist(this.script);
    }
    this.messager?.emit("run", JSON.parse(JSON.stringify(this.config)));
    this.addLog("Script is running!");
    if (this.isAutoStep) {
      this.step();
    }
  }

  step() {
    if (this.isRunning) {
      this.messager?.emit("bet");
    }
  }

  stop() {
    this.isRunning = false;
    this.game.setBetStatus(false);
    this.addLog("Script is stopped!");
    this.syncCurrency();
    if (this.messager) {
      this.messager.destroy();
      this.messager = null;
    }
  }

  addLog(message, type = ow.INFO) {
    let id = ++this.logid;
    let logs = [...this.logs, { message, type, id }];
    if (logs.length > ScriptManager.MAX_LOG) {
      logs = logs.slice(-ScriptManager.MAX_LOG);
    }
    this.logs = logs;
  }

  enableAutoStep(enable = true) {
    this.isAutoStep = enable;
    this.handleBet = enable
      ? ({ amount, payout }) =>
          this.game
            .handleScriptBet(amount, { payout })
            .then(([result, payout]) => {
              this.delay().then(this.step);
              this.onGameEnd(payout || result);
              return result;
            })
      : ({ amount, payout }) =>
          this.game
            .handleScriptBet(amount, { payout })
            .then(([result, payout]) => {
              this.onGameEnd(payout || result);
              return result;
            });
  }

  async delay() {
    let now = Date.now();
    let timeDiff = this.interval - (now - this.prevBetTime);
    await delayFunction(Math.max(timeDiff, 0));
    this.prevBetTime = Date.now();
  }
}
